// ---------- DOM uses part----------
const mainImgEl = document.getElementById('mainImgEl');
const thumbnailsEl = document.getElementById('thumbnails');
const colorSwatchesEl = document.getElementById('colorSwatches');
const sizeSelectEl = document.getElementById('sizeSelect');
const stateLabelEl = document.getElementById('stateLabel');
const productTitleEl = document.getElementById('productTitle');
const productPriceEl = document.getElementById('productPrice');

const sizeChartModal = document.getElementById('sizeChartModal');
const sizeChartBtn = document.getElementById('sizeChartBtn');
const compareBtn = document.getElementById('compareColorsBtn');
const compareModal = document.getElementById('compareModal');
const compareSwatchesEl = document.getElementById('compareSwatches');
const comparePreviewEl = document.getElementById('comparePreview');

const pairRowEl = document.getElementById('pairRow');
const relatedGridEl = document.getElementById('relatedGrid');

const addBundleBtn = document.getElementById('addBundleBtn');
const bundleTotalEl = document.getElementById('bundleTotal');


productTitleEl.textContent = product.title;
productPriceEl.textContent = `₹${product.price}`;

// thumbnails
product.images.forEach((img, idx)=>{
  const t = document.createElement('img');
  t.src = img.src;
  t.alt = product.title + ' ' + (idx+1);
  t.dataset.id = img.id;
  t.dataset.color = img.color;
  if(idx===0) t.classList.add('active');
  t.addEventListener('click', ()=> {
    setMainImage(img.src);
    document.querySelectorAll('#thumbnails img').forEach(i => i.classList.remove('active'));
    t.classList.add('active');
  });
  thumbnailsEl.appendChild(t);
});

// set first image as main
function setMainImage(src){
  mainImgEl.src = src;
}

// color swatches
product.colors.forEach((c, i)=>{
  const s = document.createElement('button');
  s.className = 'swatch';
  s.title = c.name;
  s.setAttribute('aria-pressed','false');
  s.style.background = c.value;
  s.dataset.key = c.key;
  s.addEventListener('click', ()=> {
    selectColor(c.key, s);
  });
  colorSwatchesEl.appendChild(s);
});

// compare modal
product.colors.forEach(c=>{
  const wrapper = document.createElement('label');
  wrapper.style.display = 'flex';
  wrapper.style.alignItems = 'center';
  wrapper.style.gap = '8px';
  const checkbox = document.createElement('input');
  checkbox.type = 'checkbox';
  checkbox.value = c.key;
  const sw = document.createElement('span');
  sw.className = 'swatch';
  sw.style.background = c.value;
  sw.title = c.name;
  wrapper.appendChild(checkbox);
  wrapper.appendChild(sw);
  compareSwatchesEl.appendChild(wrapper);

  checkbox.addEventListener('change', renderComparePreview);
});

function renderComparePreview(){
  comparePreviewEl.innerHTML = '';
  const checked = Array.from(compareSwatchesEl.querySelectorAll('input[type=checkbox]:checked')).map(i=>i.value);
  checked.forEach(k=>{
    const colorObj = product.colors.find(p=>p.key===k);
    const card = document.createElement('div');
    card.style.padding = '8px';
    card.style.border = '1px solid #eee';
    card.style.borderRadius = '8px';
    card.innerHTML = `<div style="width:120px;height:120px;background:${colorObj.value};border-radius:8px;"></div><div style="text-align:center;margin-top:6px">${colorObj.name}</div>`;
    comparePreviewEl.appendChild(card);
  });
}

// size select
(function populateSizes(){
  
  const savedSize = localStorage.getItem('selectedSize');
  if(savedSize){
    sizeSelectEl.value = savedSize;
  }
})();

sizeSelectEl.addEventListener('change', ()=>{
  localStorage.setItem('selectedSize', sizeSelectEl.value);
  updateStateLabel();
});

// color select  & localStorage
function selectColor(key, buttonEl){
  document.querySelectorAll('.swatch').forEach(s=>s.classList.remove('selected'));
  buttonEl.classList.add('selected');
  localStorage.setItem('selectedColor', key);
 
  const found = product.images.find(img => img.color === key);
  if(found) setMainImage(found.src);
  updateStateLabel();
}
(function restoreColor(){
  const saved = localStorage.getItem('selectedColor');
  if(saved){
    const btn = Array.from(document.querySelectorAll('#colorSwatches .swatch')).find(s=>s.dataset.key===saved);
    if(btn) btn.click();
  }
})();

function updateStateLabel(){
  const c = localStorage.getItem('selectedColor') || '—';
  const s = sizeSelectEl.value || '—';
  stateLabelEl.textContent = `Color: ${c} | Size: ${s}`;
}

// Tab functionality 
document.querySelectorAll('.tab').forEach(tab=>{
  tab.addEventListener('click', ()=>{
    document.querySelectorAll('.tab').forEach(t=>t.classList.remove('active'));
    tab.classList.add('active');
    const target = tab.dataset.tab;
    document.querySelectorAll('.tab-pane').forEach(p=>p.classList.remove('active'));
    document.getElementById(target).classList.add('active');
  });
});
