// Bundle calc (static items)
(function setBundleTotal(){
  const priceA = 499, priceB = 799;
  const total = priceA + priceB;
  bundleTotalEl.textContent = `₹${total}`;
})();
addBundleBtn.addEventListener('click', ()=> {
  alert('Bundle added to cart (simulated). Total: ' + bundleTotalEl.textContent);
});

// Modal helpers (open/close, overlay click, ESC)
function openModal(modal){
  modal.setAttribute('aria-hidden','false');
  modal.style.display='flex';
  document.body.style.overflow = 'hidden';
}
function closeModal(modal){
  modal.setAttribute('aria-hidden','true');
  modal.style.display='none';
  document.body.style.overflow = '';
}

// size chart modal
sizeChartBtn.addEventListener('click', ()=> openModal(sizeChartModal));
compareBtn.addEventListener('click', ()=> openModal(compareModal));

//overlay & close buttons uses
document.querySelectorAll('[data-close]').forEach(el=>{
  el.addEventListener('click', (e)=>{
    const parent = e.target.closest('.modal');
    if(parent) closeModal(parent);
  });
});
document.querySelectorAll('.modal-close').forEach(b=>{
  b.addEventListener('click', ()=> {
    const modal = b.closest('.modal');
    closeModal(modal);
  });
});


document.addEventListener('keydown', (e)=>{
  if(e.key === 'Escape'){
    document.querySelectorAll('.modal[aria-hidden="false"]').forEach(m=>closeModal(m));
  }
});


updateStateLabel();

//left/right on main image
document.getElementById('mainImage').addEventListener('keydown', (e)=>{
  const thumbs = Array.from(document.querySelectorAll('#thumbnails img'));
  const activeIndex = thumbs.findIndex(t => t.classList.contains('active'));
  if(e.key === 'ArrowRight'){
    const next = thumbs[(activeIndex+1) % thumbs.length];
    next.click();
  } else if(e.key === 'ArrowLeft'){
    const prev = thumbs[(activeIndex-1 + thumbs.length) % thumbs.length];
    prev.click();
  }
});
